// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

import "../dependencies/CheckContract.sol";
import "../dependencies/ERC2612Permit.sol";
import "../interfaces/IZKToken.sol";
import "../interfaces/ILockupContractFactory.sol";

contract ZKToken is IZKToken, CheckContract, ERC2612Permit {
    using SafeMath for uint256;

    uint256 public constant ONE_YEAR_IN_SECONDS = 31536000; // 60 * 60 * 24 * 365

    // uint256 for use with SafeMath
    uint256 internal _1_MILLION = 1e24; // 1e6 * 1e18 = 1e24

    uint256 internal immutable deploymentStartTime;

    address public immutable communityIssuanceAddress;
    address public immutable zktStakingAddress;

    uint256 internal immutable lpRewardsEntitlement;

    ILockupContractFactory public immutable lockupContractFactory;

    address public multisigAddress;

    constructor(
        address _communityIssuanceAddress,
        address _zktStakingAddress,
        address _lockupFactoryAddress,
        address _bountyAddress,
        address _lpRewardsAddress,
        address _multisigAddress
    ) ERC20("ZKToken", "ZKT") ERC2612Permit("ZKT") {
        checkContract(_communityIssuanceAddress);
        checkContract(_zktStakingAddress);
        checkContract(_lockupFactoryAddress);

        multisigAddress = _multisigAddress;
        deploymentStartTime = block.timestamp;

        communityIssuanceAddress = _communityIssuanceAddress;
        zktStakingAddress = _zktStakingAddress;
        lockupContractFactory = ILockupContractFactory(_lockupFactoryAddress);

        // --- Initial ZKT allocations ---

        uint256 bountyEntitlement = _1_MILLION.mul(2); // Allocate 2 million for bounties/hackathons
        _mint(_bountyAddress, bountyEntitlement);

        uint256 depositorsAndFrontEndsEntitlement = _1_MILLION.mul(32); // Allocate 32 million to the algorithmic issuance schedule
        _mint(_communityIssuanceAddress, depositorsAndFrontEndsEntitlement);

        uint256 _lpRewardsEntitlement = _1_MILLION.mul(4).div(3); // Allocate 1.33 million for LP rewards
        lpRewardsEntitlement = _lpRewardsEntitlement;
        if (_lpRewardsAddress == address(0)) {
            _mint(_multisigAddress, _lpRewardsEntitlement);
        } else {
            _mint(_lpRewardsAddress, _lpRewardsEntitlement);
        }

        // Allocate the remainder to the ZKT Multisig: (100 - 2 - 32 - 1.33) million = 64.66 million
        uint256 multisigEntitlement = _1_MILLION
            .mul(100)
            .sub(bountyEntitlement)
            .sub(depositorsAndFrontEndsEntitlement)
            .sub(_lpRewardsEntitlement);

        _mint(_multisigAddress, multisigEntitlement);
    }

    function getDeploymentStartTime() external view override returns (uint256) {
        return deploymentStartTime;
    }

    function getLpRewardsEntitlement()
        external
        view
        override
        returns (uint256)
    {
        return lpRewardsEntitlement;
    }

    function sendToZKTStaking(
        address _sender,
        uint256 _amount
    ) external override {
        _requireCallerIsZKTStaking();
        if (_isFirstYear()) {
            _requireSenderIsNotMultisig(_sender);
        } // Prevent the multisig from staking ZK
        _transfer(_sender, zktStakingAddress, _amount);
    }

    function approve(
        address spender,
        uint256 amount
    ) public override(IERC20, ERC20) returns (bool) {
        if (_isFirstYear()) {
            _requireCallerIsNotMultisig();
        }

        return super.approve(spender, amount);
    }

    function transfer(
        address recipient,
        uint256 amount
    ) public override(IERC20, ERC20) returns (bool) {
        // Restrict the multisig's transfers in first year
        if (_callerIsMultisig() && _isFirstYear()) {
            _requireRecipientIsRegisteredLC(recipient);
        }

        _requireValidRecipient(recipient);

        // Otherwise, standard transfer functionality
        return super.transfer(recipient, amount);
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override(IERC20, ERC20) returns (bool) {
        if (_isFirstYear()) {
            _requireSenderIsNotMultisig(sender);
        }

        _requireValidRecipient(recipient);

        return super.transferFrom(sender, recipient, amount);
    }

    function increaseAllowance(
        address spender,
        uint256 addedValue
    ) public override(ERC20) returns (bool) {
        if (_isFirstYear()) {
            _requireCallerIsNotMultisig();
        }

        return super.increaseAllowance(spender, addedValue);
    }

    function decreaseAllowance(
        address spender,
        uint256 subtractedValue
    ) public override(ERC20) returns (bool) {
        if (_isFirstYear()) {
            _requireCallerIsNotMultisig();
        }

        return super.decreaseAllowance(spender, subtractedValue);
    }

    // --- Helper functions ---

    function _callerIsMultisig() internal view returns (bool) {
        return (msg.sender == multisigAddress);
    }

    function _isFirstYear() internal view returns (bool) {
        return (block.timestamp.sub(deploymentStartTime) < ONE_YEAR_IN_SECONDS);
    }

    // --- 'require' functions ---

    function _requireValidRecipient(address _recipient) internal view {
        require(
            _recipient != address(0) && _recipient != address(this),
            "ZK: Cannot transfer tokens directly to the ZK token contract or the zero address"
        );
        require(
            _recipient != communityIssuanceAddress &&
                _recipient != zktStakingAddress,
            "ZK: Cannot transfer tokens directly to the community issuance or staking contract"
        );
    }

    function _requireRecipientIsRegisteredLC(address _recipient) internal view {
        require(
            lockupContractFactory.isRegisteredLockup(_recipient),
            "ZKToken: recipient must be a LockupContract registered in the Factory"
        );
    }

    function _requireSenderIsNotMultisig(address _sender) internal view {
        require(
            _sender != multisigAddress,
            "ZKToken: sender must not be the multisig"
        );
    }

    function _requireCallerIsNotMultisig() internal view {
        require(
            !_callerIsMultisig(),
            "ZKToken: caller must not be the multisig"
        );
    }

    function _requireCallerIsZKTStaking() internal view {
        require(
            msg.sender == zktStakingAddress,
            "ZKToken: caller must be the ZKTStaking contract"
        );
    }
}

contracts/common/ZKTStaking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/Math.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

import "../interfaces/ICommunityIssuance.sol";
import "../interfaces/IZKTStaking.sol";
import "../interfaces/IZKToken.sol";
import "../interfaces/IZKUSDToken.sol";
import "../dependencies/CheckContract.sol";
import "../dependencies/FullMath.sol";

contract ZKTStaking is IZKTStaking, Ownable, CheckContract {
    using SafeMath for uint256;

    // --- Data ---
    string public constant NAME = "ZKTStaking";
    uint256 public constant DECIMAL_PRECISION = 1e18;

    mapping(address => uint256) public stakes;
    uint256 public totalZKTStaked;

    uint256 public F_ETH; // Running sum of ETH fees per-ZKT-staked
    uint256 public F_ZKUSD; // Running sum of ZKT fees per-ZKT-staked

    // User snapshots of F_ETH and F_ZKUSD, taken at the point at which their latest deposit was made
    mapping(address => Snapshot) public snapshots;

    struct Snapshot {
        uint256 F_ETH_Snapshot;
        uint256 F_ZKUSD_Snapshot;
    }

    IZKToken public zkToken;
    IZKUSDToken public zkusdToken;

    address public troveManagerAddress;
    address public borrowerOperationsAddress;
    address public activePoolAddress;

    constructor(address _ownership) {
        _transferOwnership(_ownership);
    }

    function setAddresses(
        address _zkTokenAddress,
        address _zkusdTokenAddress,
        address _troveManagerAddress,
        address _borrowerOperationsAddress,
        address _activePoolAddress
    ) external override onlyOwner {
        checkContract(_zkTokenAddress);
        checkContract(_zkusdTokenAddress);
        checkContract(_troveManagerAddress);
        checkContract(_borrowerOperationsAddress);
        checkContract(_activePoolAddress);

        zkToken = IZKToken(_zkTokenAddress);
        zkusdToken = IZKUSDToken(_zkusdTokenAddress);
        troveManagerAddress = _troveManagerAddress;
        borrowerOperationsAddress = _borrowerOperationsAddress;
        activePoolAddress = _activePoolAddress;

        emit ZKTokenAddressSet(_zkTokenAddress);
        emit ZKTokenAddressSet(_zkusdTokenAddress);
        emit TroveManagerAddressSet(_troveManagerAddress);
        emit BorrowerOperationsAddressSet(_borrowerOperationsAddress);
        emit ActivePoolAddressSet(_activePoolAddress);

        //renounceOwnership();
    }

    // If caller has a pre-existing stake, send any accumulated ETH and ZKUSD gains to them.
    function stake(uint256 _ZKTamount) external override {
        _requireNonZeroAmount(_ZKTamount);

        uint256 currentStake = stakes[msg.sender];

        uint256 ETHGain;
        uint256 ZKUSDGain;
        // Grab any accumulated ETH and ZKUSD gains from the current stake
        if (currentStake != 0) {
            ETHGain = _getPendingETHGain(msg.sender);
            ZKUSDGain = _getPendingZKUSDGain(msg.sender);
        }

        _updateUserSnapshots(msg.sender);

        uint256 newStake = currentStake.add(_ZKTamount);

        // Increase user’s stake and total ZKT staked
        stakes[msg.sender] = newStake;
        totalZKTStaked = totalZKTStaked.add(_ZKTamount);
        emit TotalZKTStakedUpdated(totalZKTStaked);

        // Transfer ZKT from caller to this contract
        zkToken.sendToZKTStaking(msg.sender, _ZKTamount);

        emit StakeChanged(msg.sender, newStake);
        emit StakingGainsWithdrawn(msg.sender, ZKUSDGain, ETHGain);

        // Send accumulated ZKUSD and ETH gains to the caller
        if (currentStake != 0) {
            zkusdToken.transfer(msg.sender, ZKUSDGain);
            _sendETHGainToUser(ETHGain);
        }
    }

    // Unstake the ZKT and send the it back to the caller, along with their accumulated ZKUSD & ETH gains.
    // If requested amount > stake, send their entire stake.
    function unstake(uint256 _ZKTamount) external override {
        uint256 currentStake = stakes[msg.sender];
        _requireUserHasStake(currentStake);

        // Grab any accumulated ETH and ZKUSD gains from the current stake
        uint256 ETHGain = _getPendingETHGain(msg.sender);
        uint256 ZKUSDGain = _getPendingZKUSDGain(msg.sender);

        _updateUserSnapshots(msg.sender);

        if (_ZKTamount > 0) {
            uint256 ZKTToWithdraw = Math.min(_ZKTamount, currentStake);

            uint256 newStake = currentStake.sub(ZKTToWithdraw);

            // Decrease user's stake and total ZKT staked
            stakes[msg.sender] = newStake;
            totalZKTStaked = totalZKTStaked.sub(ZKTToWithdraw);
            emit TotalZKTStakedUpdated(totalZKTStaked);

            // Transfer unstaked ZKT to user
            zkToken.transfer(msg.sender, ZKTToWithdraw);

            emit StakeChanged(msg.sender, newStake);
        }

        emit StakingGainsWithdrawn(msg.sender, ZKUSDGain, ETHGain);

        // Send accumulated ZKUSD and ETH gains to the caller
        zkusdToken.transfer(msg.sender, ZKUSDGain);
        _sendETHGainToUser(ETHGain);
    }

    // --- Reward-per-unit-staked increase functions. Called by Liquity core contracts ---

    function increaseF_ETH(uint256 _ETHFee) external override {
        _requireCallerIsTroveManager();
        uint256 ETHFeePerZKTStaked;

        if (totalZKTStaked > 0) {
            ETHFeePerZKTStaked = _ETHFee.mul(DECIMAL_PRECISION).div(
                totalZKTStaked
            );
        }

        F_ETH = F_ETH.add(ETHFeePerZKTStaked);
        emit F_ETHUpdated(F_ETH);
    }

    function increaseF_ZKUSD(uint256 _ZKUSDFee) external override {
        _requireCallerIsBorrowerOperations();
        uint256 ZKUSDFeePerZKTStaked;

        if (totalZKTStaked > 0) {
            ZKUSDFeePerZKTStaked = _ZKUSDFee.mul(DECIMAL_PRECISION).div(
                totalZKTStaked
            );
        }

        F_ZKUSD = F_ZKUSD.add(ZKUSDFeePerZKTStaked);
        emit F_ZKUSDUpdated(F_ZKUSD);
    }

    // --- Pending reward functions ---

    function getPendingETHGain(
        address _user
    ) external view override returns (uint256) {
        return _getPendingETHGain(_user);
    }

    function _getPendingETHGain(address _user) internal view returns (uint256) {
        uint256 F_ETH_Snapshot = snapshots[_user].F_ETH_Snapshot;
        uint256 ETHGain = stakes[_user].mul(F_ETH.sub(F_ETH_Snapshot)).div(
            DECIMAL_PRECISION
        );
        return ETHGain;
    }

    function getPendingZKUSDGain(
        address _user
    ) external view override returns (uint256) {
        return _getPendingZKUSDGain(_user);
    }

    function _getPendingZKUSDGain(
        address _user
    ) internal view returns (uint256) {
        uint256 F_ZKUSD_Snapshot = snapshots[_user].F_ZKUSD_Snapshot;
        uint256 ZKUSDGain = stakes[_user]
            .mul(F_ZKUSD.sub(F_ZKUSD_Snapshot))
            .div(DECIMAL_PRECISION);
        return ZKUSDGain;
    }

    // --- Internal helper functions ---

    function _updateUserSnapshots(address _user) internal {
        snapshots[_user].F_ETH_Snapshot = F_ETH;
        snapshots[_user].F_ZKUSD_Snapshot = F_ZKUSD;
        emit StakerSnapshotsUpdated(_user, F_ETH, F_ZKUSD);
    }

    function _sendETHGainToUser(uint256 ETHGain) internal {
        emit EtherSent(msg.sender, ETHGain);
        (bool success, ) = msg.sender.call{value: ETHGain}("");
        require(success, "ZKTStaking: Failed to send accumulated ETHGain");
    }

    // --- 'require' functions ---

    function _requireCallerIsTroveManager() internal view {
        require(
            msg.sender == troveManagerAddress,
            "ZKTStaking: caller is not TroveM"
        );
    }

    function _requireCallerIsBorrowerOperations() internal view {
        require(
            msg.sender == borrowerOperationsAddress,
            "ZKTStaking: caller is not BorrowerOps"
        );
    }

    function _requireCallerIsActivePool() internal view {
        require(
            msg.sender == activePoolAddress,
            "ZKTStaking: caller is not ActivePool"
        );
    }

    function _requireUserHasStake(uint256 currentStake) internal pure {
        require(
            currentStake > 0,
            "ZKTStaking: User must have a non-zero stake"
        );
    }

    function _requireNonZeroAmount(uint256 _amount) internal pure {
        require(_amount > 0, "ZKTStaking: Amount must be non-zero");
    }

    receive() external payable {
        _requireCallerIsActivePool();
    }
}

