// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

// Common interface for the Pools.
interface IPool {
    // --- Events ---

    event ETHBalanceUpdated(uint256 _newBalance);
    event ZKUSDBalanceUpdated(uint256 _newBalance);
